window.loadScreenStart=async function(){$("body").prepend('<div id="loadScreen"></div>')},window.loadScreenEnd=async function(){$("#loadScreen").fadeOut("fast",(function(){$(this).remove()}))},window.SweetToast=async function(o,e){Swal.fire({title:o,icon:e,toast:!0,position:"top-end",showConfirmButton:!1,timer:5e3,timerProgressBar:!0,didOpen:o=>{o.onmouseenter=Swal.stopTimer,o.onmouseleave=Swal.resumeTimer}})},window.SweetModal=async function(o,e){loadScreenStart(),$.ajax({url:e,error:o=>{Swal.fire({title:"An Error Occured",html:"error: "+o,icon:"error",toast:!0,position:"top-end",showCancelButton:!0}),loadScreenEnd()},success:function(e){e&&(Swal.fire({title:o,html:e,position:"top",showConfirmButton:!0,confirmButtonText:"Confirm",showCancelButton:!0,allowOutsideClick:!1,allowEscapeKey:!1}),loadScreenEnd())}})};